        sources: [{ "id": "57", "text": "Resveratrol and pterostilbene: A comparative overview of their chemistry, biosynthesis, plant sources and pharmacological properties \\- Journal of Applied Pharmaceutical Science", "url": "https://japsonline.com/abstract.php?article\\_id=2952\\&sts=2" }, { "id": "59", "text": "Sirtuin activators as an anti-aging intervention for longevity \\- Open Exploration Publishing", "url": "https://www.explorationpub.com/Journals/eds/Article/100881" }, { "id": "80", "text": "5-HTP: Overview, Uses, Side Effects, Precautions, Interactions, Dosing and Reviews", "url": "https://www.webmd.com/vitamins/ai/ingredientmono-794/5-htp" }],
